mapper.update:
/* SampleCodeMapper.updateCommonCodeData */
UPDATE TB_COMMON_CODE
   SET COMMON_CODE_NM = #{commonCodeNm}
	 , USE_YN = #{useYn}
	 , MOD_USER = 'nhcadmin'
	 , MOD_DATE = SYSTIMESTAMP
 WHERE COMMON_CODE_ID = #{commonCodeId}

mapper.delete:
/* SampleCodeMapper.deleteCommonCodeData */
DELETE FROM TB_COMMON_CODE
 WHERE COMMON_CODE_ID = #{commonCodeId}

/* SampleCodeMapper.deleteCodeDetailList */
DELETE FROM TB_CODE_DETAIL
 WHERE COMMON_CODE_ID = #{commonCodeId}

mapper.insert:
/* SampleCodeMapper.insertCodeDetailData */
INSERT
  INTO TB_CODE_DETAIL
     (
       COMMON_CODE_ID
	 , CODE
	 , CODE_NM
	 , CODE_SORT
	 , USE_YN
	 , ATTR1
	 , ATTR2
	 , ATTR3
	 , ATTR4
	 , ATTR5
	 , CODE_DESC
     , CRE_USER
	 , CRE_DATE
	 , MOD_USER
	 , MOD_DATE
	 )
VALUES
	 (
	   #{commonCodeId}
	 , #{code}
	 , #{codeNm}
	 , #{codeSort}
	 , #{useYn}
	 , #{attr1}
	 , #{attr2}
	 , #{attr3}
	 , #{attr4}
	 , #{attr5}
	 , #{codeDesc}
	 , 'nhcadmin'
	 , SYSTIMESTAMP
	 , 'nhcadmin'
	 , SYSTIMESTAMP
	 )

mapper.update:
/* SampleCodeMapper.updateCodeDetailData */
UPDATE TB_CODE_DETAIL
   SET CODE_NM = #{codeNm}
	 , CODE_SORT = #{codeSort}
	 , USE_YN = #{useYn}
	 , ATTR1 = #{attr1}
	 , ATTR2 = #{attr2}
	 , ATTR3 = #{attr3}
	 , ATTR4 = #{attr4}
	 , ATTR5 = #{attr5}
	 , CODE_DESC = #{codeDesc}
     , MOD_USER = 'nhcadmin'
	 , MOD_DATE = SYSTIMESTAMP
 WHERE COMMON_CODE_ID = #{commonCodeId}
   AND CODE = #{code}

mapper.delete:
/* SampleCodeMapper.deleteCodeDetailData */
DELETE FROM TB_CODE_DETAIL
 WHERE COMMON_CODE_ID = #{commonCodeId}
   AND CODE = #{code}
