mapper.update:
    	/* SampleMapper.updateSampleData */
        UPDATE LAPTOP
           SET VENDOR       = #{vendor}
             , DISPLAY_SIZE = #{displaySize}
             , MOD_USER		= 'prunus'
			 , MOD_DATE		= SYSTIMESTAMP
			 , MOD_ADDR		= '127.0.0.1'
			 , MOD_DEPT		= NULL
         WHERE ID           = #{id}

mapper.delete:
	/* SampleMapper.deleteSampleData */
    UPDATE LAPTOP
       SET DELETED = 1
     WHERE ID      		= #{id}
